INSERT INTO va_payment_systems (payment_id,payment_order,payment_name,user_payment_name,payment_info,payment_notes,payment_url,processing_time,processing_fee,fee_type,fee_min_goods,fee_max_goods,submit_method,recurring_method,is_advanced,advanced_url,advanced_php_lib,capture_php_lib,refund_php_lib,void_php_lib,success_status_id,pending_status_id,failure_status_id,failure_action,is_active,is_default,is_call_center,sites_all,user_types_all,image_small,image_small_alt,image_large,image_large_alt,fast_checkout_active,fast_checkout_image,fast_checkout_width,fast_checkout_height,fast_checkout_alt) VALUES (2 , 1 , 'VeriSign PayFlow Link' , NULL , NULL , 'Please specify value for parameters  \'LOGIN\' and \'PARTNER\'.\r\nIf you setup your Verisign PayFlow Link account directly through Verisign, than your Partner ID is likely "Verisign" or "Paypal"  (without the quotes).\r\n\r\nTo configure Payflow Link, log in to the PayPal Manager at https://manager.paypal.com. \r\nNavigate to Service Settings > Payflow Link and click on the Configuration page.\r\n\r\nRecommended configuration: \r\nReturned URL Method  redirect to web site\r\nReturned URL - path to order_final.php on your web server, e.g. http://www.example.com/order_final.php\r\nSilent Post URL  On\r\nForce Silent Post Confirmation - Off' , 'https://payments.verisign.com/payflowlink' , NULL , NULL , NULL , NULL , NULL , 'GET' , 0 , 0 , NULL , NULL , NULL , NULL , NULL , NULL , NULL , NULL , NULL , 0 , 0 , 0 , 1 , 1 , NULL , NULL , NULL , NULL , NULL , NULL , NULL , NULL , NULL ) ;
INSERT INTO va_payment_systems (payment_id,payment_order,payment_name,user_payment_name,payment_info,payment_notes,payment_url,processing_time,processing_fee,fee_type,fee_min_goods,fee_max_goods,submit_method,recurring_method,is_advanced,advanced_url,advanced_php_lib,capture_php_lib,refund_php_lib,void_php_lib,success_status_id,pending_status_id,failure_status_id,failure_action,is_active,is_default,is_call_center,sites_all,user_types_all,image_small,image_small_alt,image_large,image_large_alt,fast_checkout_active,fast_checkout_image,fast_checkout_width,fast_checkout_height,fast_checkout_alt) VALUES (3 , 1 , 'Authorize.Net SIM' , NULL , NULL , 'Please specify values for parameters \'x_login\' and \'x_tran_key\'.\r\n\r\nPlease also set parameter \'x_test_request\' to \'FALSE\' if you are ready to run live transactions or leave it as \'TRUE\' for test your site before going live.\r\n\r\nIf you want receive response from Authorize.net about submiited transactions please also specify \'x_secret\' parameter to the same value as in your Authorize settings.\r\n\r\nTo choose your MD5 Hash Secret in the Authorize.net account please do the following:\r\n- Log into your merchant menu at https://secure.authorize.net/ \r\n- Click "Settings"\r\n- Select "MD5 Hash" under the "Security" portion of your account settings\r\n- Enter the MD5 Hash Secret value that you would like to use\r\n- Confirm the MD5 Hash value\r\n- Click "Submit" to save the changes\r\n\r\nAlso you need specify response URL in your Authorize.net account:\r\n- Select "Response/Receipt URLs" under the "Transaction Response" portion of your account settings\r\n- Click on "Edit" link for "Default Relay Response URL"\r\n- Enter your response URL (for this url you can use following page \'http://your_site_url/order_final.php\')\r\n- Click "Submit" to save the changes' , 'https://secure.authorize.net/gateway/transact.dll' , NULL , NULL , NULL , NULL , NULL , 'POST' , 0 , 0 , NULL , NULL , NULL , NULL , NULL , NULL , NULL , NULL , NULL , 0 , 0 , 0 , 1 , 1 , NULL , NULL , NULL , NULL , NULL , NULL , NULL , NULL , NULL ) ;
INSERT INTO va_payment_systems (payment_id,payment_order,payment_name,user_payment_name,payment_info,payment_notes,payment_url,processing_time,processing_fee,fee_type,fee_min_goods,fee_max_goods,submit_method,recurring_method,is_advanced,advanced_url,advanced_php_lib,capture_php_lib,refund_php_lib,void_php_lib,success_status_id,pending_status_id,failure_status_id,failure_action,is_active,is_default,is_call_center,sites_all,user_types_all,image_small,image_small_alt,image_large,image_large_alt,fast_checkout_active,fast_checkout_image,fast_checkout_width,fast_checkout_height,fast_checkout_alt) VALUES (4 , 1 , 'WorldPay' , NULL , NULL , 'Please specify your installation ID for parameter \'instId\'.\r\n\r\nAs well you can specify callbackPW parameter if you set Callback password for your installation on the WorldPay Customer Management System. As the value of this is only known to this and your callback script it can be used as a basic security check within your script.\r\nAlways leave \'Not Passed\' checkbox checked for callbackPW parameter.\r\n\r\nA quote from WorldPay FAQ.\r\n\r\nQ: How can I configure WorldPay to use callback for an installation ID? \r\nA: You will initially need to log in to the WorldPay Customer Management System (via http://www.worldpay.com/admin). In the new window that opens you will need to scroll down to the section headed Installations and select the Configuration options button corresponding to the instId you are using. \r\nThe minimum settings you need to complete are: \r\nCallback URL - This should be set to the complete address to your callback URL, hosted on your server. \r\nCallback enabled? - This determines whether WorldPay will attempt to use the callback URL when needed. \r\nIn order to use callback you must selected at least these two settings. \r\nIf you have developed your callback script to output an HTML response and would like WorldPay to capture this for display to the shopper after the callback has completed successfully, you should also ensure Use callback response? is selected. \r\nTo commit the changes you have made, select SAVE CHANGES at the bottom of the page.\r\n\r\nthe callback URL should have the value like: http://www.yoursite.com/payments/worldpay_confirm.php' , 'https://select.worldpay.com/wcc/purchase' , NULL , NULL , NULL , NULL , NULL , 'POST' , 0 , 0 , NULL , NULL , NULL , NULL , NULL , NULL , NULL , NULL , NULL , 0 , 0 , 0 , 1 , 1 , NULL , NULL , NULL , NULL , NULL , NULL , NULL , NULL , NULL ) ;
INSERT INTO va_payment_systems (payment_id,payment_order,payment_name,user_payment_name,payment_info,payment_notes,payment_url,processing_time,processing_fee,fee_type,fee_min_goods,fee_max_goods,submit_method,recurring_method,is_advanced,advanced_url,advanced_php_lib,capture_php_lib,refund_php_lib,void_php_lib,success_status_id,pending_status_id,failure_status_id,failure_action,is_active,is_default,is_call_center,sites_all,user_types_all,image_small,image_small_alt,image_large,image_large_alt,fast_checkout_active,fast_checkout_image,fast_checkout_width,fast_checkout_height,fast_checkout_alt) VALUES (5 , 1 , '2CheckOut' , NULL , NULL , 'Please specify values for parameters \'sid\' and \'secret_word\'.\r\n\'sid\' - it is your vendor number in system like 340000, \r\n\'secret_word\' - must be the same as in your 2CheckOut settings.\r\nFor Return URL you can use following page \'http://www.example.com/order_final.php\'.' , 'https://www2.2checkout.com/2co/buyer/purchase' , NULL , NULL , NULL , NULL , NULL , 'POST' , NULL , 0 , NULL , NULL , NULL , NULL , NULL , NULL , NULL , NULL , NULL , 0 , 0 , 0 , 1 , 1 , NULL , NULL , NULL , NULL , NULL , NULL , NULL , NULL , NULL ) ;
INSERT INTO va_payment_systems (payment_id,payment_order,payment_name,user_payment_name,payment_info,payment_notes,payment_url,processing_time,processing_fee,fee_type,fee_min_goods,fee_max_goods,submit_method,recurring_method,is_advanced,advanced_url,advanced_php_lib,capture_php_lib,refund_php_lib,void_php_lib,success_status_id,pending_status_id,failure_status_id,failure_action,is_active,is_default,is_call_center,sites_all,user_types_all,image_small,image_small_alt,image_large,image_large_alt,fast_checkout_active,fast_checkout_image,fast_checkout_width,fast_checkout_height,fast_checkout_alt) VALUES (6 , 1 , 'PayPal' , NULL , NULL , 'Please specify your business email address where it says \'your.paypal@example.com\' and specify value for \'at\' (identity token) parameter from your PayPal settings.\r\n\r\nActivating PDT.\r\nTo use PDT, you must activate PDT and Auto Return in your PayPal account profile. You must also acquire a PDT identity token, which is used in all PDT communication you send to PayPal.\r\n \r\nFollow these steps to configure your account for PDT:\r\n1. Log in to your PayPal account. \r\n2. Click the Profile sub tab. \r\n3. Click Website Payment Preferences in the Seller Preferences column. \r\n4. Under Auto Return for Website Payments, click the On radio button. \r\n5. For the Return URL, enter the URL on your site that will receive the transaction ID posted by PayPal after a customer payment as \'http://example.com/order_final.php\'.\r\n6. Under Payment Data Transfer, click the On radio button. \r\n7. Click Save. \r\n8. Click Website Payment Preferences in the Seller Preferences column. \r\n9. Scroll down to the Payment Data Transfer section of the page to view your PDT identity token, copy it and paste as \'at\' parameter value.\r\n\r\nAlso it\'s recommended to turn on PayPal IPN notifications. For this withing you PayPal account please go to \'Profile > Instant Payment Notification Preferences\' and turn on this setting and set \'Notification URL\' as \'http://example.com/payments/paypal_ipn.php\'.\r\n\r\nIf you don\'t want pass data to PayPal in multi currencies please specify for currency_code parameter preferred currency code like \'USD\'.\r\n\r\nFor testing with PayPal SandBox please change \'sandbox\' parameter value to \'1\' and specify \'Payment URL\' as https://www.sandbox.paypal.com/cgi-bin/webscr\r\n\r\nAfter testing with Sandbox please return \'sandbox\' value to \'0\' and specify production \'Payment URL\': https://www.paypal.com/cgi-bin/webscr' , 'https://www.paypal.com/cgi-bin/webscr' , NULL , NULL , NULL , NULL , NULL , 'POST' , 0 , 0 , NULL , NULL , NULL , NULL , NULL , NULL , NULL , NULL , NULL , 1 , 0 , 0 , 1 , 1 , NULL , NULL , NULL , NULL , 0 , NULL , NULL , NULL , NULL ) ;
