INSERT INTO va_payment_systems (payment_id,payment_order,payment_name,user_payment_name,payment_info,payment_notes,payment_url,processing_time,processing_fee,fee_type,fee_min_goods,fee_max_goods,submit_method,recurring_method,is_advanced,advanced_url,advanced_php_lib,capture_php_lib,refund_php_lib,void_php_lib,success_status_id,pending_status_id,failure_status_id,failure_action,is_active,is_default,is_call_center,sites_all,user_types_all,image_small,image_small_alt,image_large,image_large_alt,fast_checkout_active,fast_checkout_image,fast_checkout_width,fast_checkout_height,fast_checkout_alt) VALUES (43 , 1 , 'MoneyBookers' , NULL , NULL , 'Production URL: https://www.moneybookers.com/app/payment.pl\r\nTest URL: https://moneybookers.com/app/test_payment.pl\r\n\r\nRequired fields:\r\n\r\npay_to_email - Email address of the merchants moneybookers.com account\r\n\r\nsecret_word (required for moneybookers_status.php)\r\nThe secret word MUST be submitted in the Merchant Tools section in lowercase before the md5sig can be used. If you insert any uppercase symbols, they will automatically be converted to lower case. The only restriction on your secret word is the length which must not exceed 10 characters. Non-alphanumeric symbols can be used. If the Merchant Tools section is not shown in your account, please contact merchantservices@moneybookers.com\r\n------------------------------------------------------------------------------------------------------\r\nOther fields:\r\n\r\nconfirmation_note\r\nMerchant may show to the customer on the confirmation screen - the end step of the\r\nprocess - a note, confirmation number, PIN or any other message. Line breaks <br> may be used for longer messages.\r\n\r\nreturn_url\r\nURL to which the customer will be returned when the payment is made. If this field is not filled, the merchant gateway window will simply close automatically at the end of the transaction, so that the customer will be returned to the last page on the merchant\'s website where he has been before. A secure return_url functionality is available.\r\n\r\ncancel_url\r\nURL to which the customer will be returned if the payment process is cancelled. If this field is not filled, the merchant gateway window will simply close automatically upon clicking the cancellation button, so the customer will be\r\nreturned to the last page on the merchant\'s website where the customer has been before.\r\n\r\nstatus_url\r\nURL to which the transaction details will posted after the payment process is complete.\r\nAlternatively, you may specify an email to which you would like to receive the If the status_url is omitted, no transaction details will be sent to the merchant.\r\nCould be email like  EMAIL_TO_RECEIVE_PAYMENT_NOTIFICATION@MERCHANT.COM' , 'https://www.moneybookers.com/app/payment.pl' , NULL , NULL , NULL , NULL , NULL , 'POST' , 0 , 0 , NULL , NULL , NULL , NULL , NULL , NULL , NULL , NULL , NULL , 0 , 0 , 0 , 1 , 1 , 'http://www.moneybookers.com/images/logos/checkout_logos/checkout_120x40px.gif' , NULL , 'http://www.moneybookers.com/images/logos/checkout_logos/checkout_180x60px.gif' , NULL , 0 , NULL , NULL , NULL , NULL ) ;
